import React, { useState, useEffect } from "react";
import Cell from "./Cell";
import "./Table.css";

const App = () => {
  const [turn, setTurn] = useState("O");
  const [cells, setCells] = useState(Array(9).fill(""));
  const [results, setResults] = useState("");

  const winningNumbers = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];

  useEffect(() => {
    if (!cells.find((c) => c === "")) {
      setResults("Results: Tie");
    }
    for (let i = 0; i < winningNumbers.length; i++) {
      const [a, b, c] = winningNumbers[i];
      if (cells[a] && cells[a] === cells[b] && cells[b] === cells[c]) {
        setResults(`Results: ${cells[a]}`);
        break;
      }
    }
  }, [cells]);

  const restartGame = () => {
    if (turn === "X") {
      setTurn("O");
    } else {
      setTurn("X");
    }
    setResults("");
    setCells(Array(9).fill(""));
  };

  return (
    <div className="container">
      <span className="turnShow">Turn: {turn}!</span>
      <table className="table">
        <tbody>
          {[0, 1, 2].map((n) => (
            <tr>
              {[3 * n + 0, 3 * n + 1, 3 * n + 3].map((id) => (
                <Cell key={id} onClick={() => handleClick(id)}>
                  {cells[id]}
                </Cell>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <div>{results}</div>
      <button
        className={results !== "" ? "green" : "red"}
        onClick={() => restartGame()}
      >
        {" "}
        {results !== "" ? "Play Again" : "Reset"}
      </button>
    </div>
  );
};

export default App;
