import React, { useState } from 'react';

const Cell = ({ id }) => {
    const [turn, setTurn] = useState('X');
    const [cells, setCells] = (Array(9).fill(''));

    const handleClick = (id) => {
        if (turn === 'X') {
            setCells[id]('O');
            setTurn('X');
        } else {
            setCells[id]('X');
            setTurn('O');
        }
    };
        return (
            <td className="cell" onClick={() => handleClick(id)}>{cells[id]}</td>
        );
}

export default Cell;
