import React, { useState } from "react";
import Cell from "./Cell";

const Table = () => {
  const [turn, setTurn] = useState("O");
  const [cells, setCells] = useState(Array(9).fill(""));

  const results = useEffect(() => {
    const winningNumbers = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];
    for (let i = 0; i < winningNumbers.length; i++) {
      const [a, b, c] = winningNumbers[i];
      if (cells[a] && cells[a] === cells[b] && cells[b] === cells[c]) {
        return `Results: ${cells[a]}`;
        break;
      } else if (
        (cells[0] &&
          cells[1] &&
          cells[2] &&
          cells[3] &&
          cells[4] &&
          cells[5] &&
          cells[6] &&
          cells[7] &&
          cells[8]) === ""
      ) {
        return "";
      } else {
        return "Results: Tie";
      }
    }
  }, [cells]);

  const restartGame = () => {
    if (turn === "X") {
      setTurn("O");
    } else {
      setTurn("X");
    }
    setResults("");
    setCells(Array(9).fill(""));
  };

  const cellFormat = (id) => {
    return (
      <Cell
        id={id}
        cells={cells}
        results={results}
        setCells={setCells}
        turn={turn}
        setTurn={setTurn}
      />
    );
  };

  return (
    <div className="container">
      <span className="turnShow">Turn: {turn}!</span>
      <table className="table">
        <tbody>
          <tr>
            {cellFormat(0)}
            {cellFormat(1)}
            {cellFormat(2)}
          </tr>
          <tr>
            {cellFormat(3)}
            {cellFormat(4)}
            {cellFormat(5)}
          </tr>
          <tr>
            {cellFormat(6)}
            {cellFormat(7)}
            {cellFormat(8)}
          </tr>
        </tbody>
      </table>
      <div>{results}</div>
      <button
        className={results !== "" ? "green" : "red"}
        onClick={() => restartGame()}
      >
        {" "}
        {results !== "" ? "Play Again" : "Reset"}
      </button>
    </div>
  );
};

export default Table;
