import React, { useState } from "react";
import Cell from "./Cell";
import Result from "./Result";
import "./Table.css";
const App = () => {
  const [turn, setTurn] = useState("O");
  const [cells, setCells] = useState(Array(9).fill(""));
  const [results, setResults] = useState("");

  const restartGame = () => {
    if (turn === "X") {
      setTurn("O");
    } else {
      setTurn("X");
    }
    setResults("");
    setCells(Array(9).fill(""));
  };

  const cellFormat = (id) => {
    return (
      <Cell
        id={id}
        cells={cells}
        results={results}
        setCells={setCells}
        turn={turn}
        setTurn={setTurn}
      />
    );
  };

  return (
    <div className="container">
      <span className="turnShow">Turn: {turn}!</span>
      <table className="table">
        <tbody>
          <tr>
            {cellFormat(0)}
            {cellFormat(1)}
            {cellFormat(2)}
          </tr>
          <tr>
            {cellFormat(3)}
            {cellFormat(4)}
            {cellFormat(5)}
          </tr>
          <tr>
            {cellFormat(6)}
            {cellFormat(7)}
            {cellFormat(8)}
          </tr>
        </tbody>
      </table>

      <Result cells={cells} results={results} setResults={setResults}></Result>

      <button
        className={results !== "" ? "green" : "red"}
        onClick={() => restartGame()}
      >
        {" "}
        {results !== "" ? "Play Again" : "Reset"}
      </button>
    </div>
  );
};

export default App;
